//Program to demonstrate enhanced for loop
package org.tnsif.looping;

public class EnhancedforloopProgram {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char array[]= {'a','b','c','d'};
		
		//enhanced for loop
		for(int xyz:array) {
			System.out.println(xyz+" ");
		}
	}

}
